import React,{useEffect, useState} from 'react'
import {Link, Route, Routes} from "react-router-dom"
import './App.css';
import Header from "./components/Header/Header";
import Footer from "./components/Footer/Footer";
import AntiqueTable from './components/ATable/AntiqueTable';
import { listAntiques } from './utils/api';
import AddAntique from './components/AddAntique';
import NotFound from './components//NotFound'


function App() {
  const [antiquesError,setAntiquesError]= useState([])
  const [antiques,setAntiques]= useState([])

  useEffect(()=>{
    function loadAntiques(){
      const ac = new AbortController();
      setAntiquesError(null);
      listAntiques(ac.signal)
       .then(setAntiques)
       .catch(setAntiquesError)
        return () => ac.abort()}
        loadAntiques();
      }
  )

  return (
    <div className={"app-container"}>
      {/* <Link to="/" >Add Antique</Link> */}
      <Header />
      <Routes>

        <Route path="/" element={
      <div>
      <div className="button"><Link>New Antique</Link></div>
        <AntiqueTable antiques={antiques} />
      </div>
        } />

        <Route  path='/antiques/new' element={<AddAntique />}/>
        <Route path='*' element={<NotFound />} />
      </Routes>
      <Footer />

    </div>
  );
}

export default App;
