import { useEffect, useState } from "react"
import { useParams } from "react-router";
import { findAntique, updateAntique } from "../../utils/api";

export default function EditAntique(){

const [errors,setErrors] = useState(null);
const [antique,setAntique] = useState(null);
const {id} = useParams();

useEffect(()=>{
    async function loadAntique(){
        let ac= new AbortController();
        try{
            console.log("tryblock loadAntique")
            let SingleAntique = await findAntique(id,ac.signal)
            setAntique(SingleAntique)
        } catch(err){setErrors(err)}
        return ()=> ac.abort();
    }
    loadAntique();
},[id])

console.log(antique,"edit Antique antique")
const {category,quantity,name,value,material,model,color,style,brand,timePeriod,condition,origin,purpose,owner,history,size,comment} = antique;

const loadedAntiqueFields = {
    // id:id,
    // category:category,
    // quantity:quantity,
    // name:name,
    // value:value,
    // material:material,
    // color:color,
    // style:style,
    // brand:brand,
    // model:model,
    // timePeriod:timePeriod,
    // condition:condition,
    // origin:origin,
    // purpose:purpose,
    // owner:owner,
    // history:history,
    // size:size,
    // comment:comment,
 }
 const [form,setForm] = useState(loadedAntiqueFields)
 
 const changeHandler = e =>{
     setForm({...form,[e.target.name]:e.target.value})
    }
    
    async function submitHandler(form){
        const ac = new AbortController();
        try{
            await updateAntique(form,ac.signal);
            console.log("updated antique ",id)
        } catch(err){
            setErrors(err);
            throw err;
        }
        return ()=> ac.abort
    }

    return (
    <div>

    {antique && <div className="form-container">
    <h3 className="title-container">Edit Your Antique</h3>
    <form onSubmit={(e)=>{
        e.preventDefault();
        console.log('submit Clicked',form)
        submitHandler(form);
        // setForm(initialFields)
        // navigate.push('/antiques')
    }}>
    <div className="inputList-container">
      <div><input id="id"   name="id"    type="text" placeholder="ID"    className="inputList-input" onChange={changeHandler} value={form.id}    maxLength="20"    aria-label="category input box" /></div>
      <div><input id="category"   name="category"    type="text" placeholder="Category"    className="inputList-input" onChange={changeHandler} value={form.category}    maxLength="20"    aria-label="category input box" /></div>
      <div><input id="quantity"   name="quantity"    type="text" placeholder="Quantity"    className="inputList-input" onChange={changeHandler} value={form.quantity}    maxLength="20"    aria-label="quantity input box" /></div>
      <div><input id="name"       name="name"        type="text" placeholder="Name"        className="inputList-input" onChange={changeHandler} value={form.name}        maxLength="20"    aria-label="name input box" /></div>
      <div><input id="value"      name="value"       type="text" placeholder="Value"       className="inputList-input" onChange={changeHandler} value={form.value}       maxLength="20"    aria-label="value input box" /></div>
      <div><input id="material"   name="material"    type="text" placeholder="Material"    className="inputList-input" onChange={changeHandler} value={form.material}    maxLength="20"    aria-label="material input box"/></div>
      <div><input id="color"      name="color"       type="text" placeholder="Color"       className="inputList-input" onChange={changeHandler} value={form.color}       maxLength="20"    aria-label="color input box" /></div>
      <div><input id="style"      name="style"       type="text" placeholder="Style"       className="inputList-input" onChange={changeHandler} value={form.style}       maxLength="20"    aria-label="style input box" /></div>
      <div><input id="brand"      name="brand"       type="text" placeholder="Brand"       className="inputList-input" onChange={changeHandler} value={form.brand}       maxLength="20"    aria-label="brand input box" /></div>
      <div><input id="model"      name="model"       type="text" placeholder="Model"       className="inputList-input" onChange={changeHandler} value={form.model}       maxLength="20"    aria-label="model input box" /></div>
      <div><input id="timePeriod" name="timePeriod"  type="text" placeholder="Time Period" className="inputList-input" onChange={changeHandler} value={form.timePeriod}  maxLength="20"    aria-label="time period input box" /></div>
      <div><input id="condition"  name="condition"   type="text" placeholder="Condition"   className="inputList-input" onChange={changeHandler} value={form.condition}   maxLength="20"    aria-label="condition input box" /></div>
      <div><input id="origin"     name="origin"      type="text" placeholder="Origin"      className="inputList-input" onChange={changeHandler} value={form.origin}      maxLength="20"    aria-label="origin input box" /></div>
      <div><input id="purpose"    name="purpose"     type="text" placeholder="Purpose"     className="inputList-input" onChange={changeHandler} value={form.purpose}     maxLength="50"    aria-label="purpose input box" /></div>
      <div><input id="owner"      name="owner"       type="text" placeholder="Owner"       className="inputList-input" onChange={changeHandler} value={form.owner}       maxLength="20"    aria-label="owner input box" /></div>
      <div><input id="history"    name="history"     type="text" placeholder="History"     className="inputList-input" onChange={changeHandler} value={form.history}     maxLength="100"   aria-label="history input box" /></div>
      <div><input id="size"       name="size"        type="text" placeholder="Size"        className="inputList-input" onChange={changeHandler} value={form.size}        maxLength="20"    aria-label="size input box" /></div>
      <div><input id="comment"    name="comment"     type="text" placeholder="Comment"     className="inputList-input" onChange={changeHandler} value={form.comment}     maxLength="100"   aria-label="comment input box" /> </div>
    </div>
    <button className="inputList-btn">Submit</button>
    </form>
    </div> }
</div>
)
}
