import './AntiqueTable.css'
import {v4 as uuidv4} from 'uuid';
// import { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { deleteAntique } from '../../utils/api';


export default function AntiqueTable({antiques}){
navigate=useNavigate()

//this was for UUID


async function deleteAntiqueHandler(antiqueName){
    try{
        let ac = new AbortController();
        if(window.confirm("Are you sure you want to delete?")){
            await deleteAntique(antiqueName,ac.signal)
            .then(navigate("/antiques"))
            .catch(console.log);
        }
    } catch (err){
        
    }
}




const antiquesList =antiques?.map((antique,index)=>{
    // console.log(antique)
    const {name,category,value,material,color,size,style,brand,model,timePeriod,condition,origin,purpose,owner,history,comment} = antique; 
    return(
        <tr
        key={uuidv4()}
     className={"tableRow"}   >
        <td>{index}</td>
        <td>{name}</td>
        <td>{category}</td>
        <td>{value}</td>
        <td>{material}</td>
        <td>{color}</td>
        <td>{style}</td>
        <td>{brand}</td>
        <td>{model}</td>
        <td>{timePeriod}</td>
        <td>{condition}</td>
        <td>{origin}</td>
        <td>{purpose}</td>
        <td>{owner}</td>
        <td>{history}</td>
        <td>{size}</td>
        <td>{comment}</td>
        <td><button onClick={deleteAntiqueHandler(name)}>delete</button></td>
    </tr>)
}
    )
    // console.log("antiquesList")
    return(
        
        <div className='page-container'>
        <Link to='/antiques/addantique' className='btn'>Add Antique</Link>
        <div className={"table-container"}>
           <table className={"table"}>
            <thead className={''}>
                <tr className={'tableRow'}>
                    <th>Id</th>
                    <th>Name</th>
                    <th>Category</th>
                    <th>Value</th>
                    <th>Material</th>
                    <th>Color</th>
                    <th>Style</th>
                    <th>Brand</th>
                    <th>Model</th>
                    <th>Time Period</th>
                    <th>Condition</th>
                    <th>Origin</th>
                    <th>Purpose</th>
                    <th>Owner</th>
                    <th>History</th>
                    <th>Size</th>
                    <th>Comment</th>
                    <th>Actions</th>
                </tr>
                </thead>
                <tbody className={''}>
                    {antiquesList}
                </tbody>
                </table>
           
    
    </div>
        </div>
    )
}
