import { createAntique } from "../../utils/api";
import React,{useState} from "react";
import ErrorAlert from "../commonFiles/ErrorAlert";
import AntiqueForm from "./AntiqueForm";
import './AddAntique.css'

export default function AddAntique(){
const [apiErrs,setApiErrs] = useState(null);
    async function createSubmitHandler(form){
        const ac= new AbortController();
        try{
            console.log(form,'form addAntique.js')
            await createAntique(form,ac.signal);
            console.log('created new antique!!!')
        } catch (err){
            setApiErrs(err);
            console.log(err);
            throw err;
        }
        return () => ac.abort();
    }

    const initialFields = {
        category:'',
        quantity:'',
        name:'',
        value:'',
        material:'',
        color:'',
        style:'',
        brand:'',
        model:'',
        timePeriod:'',
        condition:'',
        origin:'',
        purpose:'',
        owner:'',
        history:'',
        size:'',
        comment:'',
     }

    return (
    <div className="AddAntique-container">
        <ErrorAlert err={apiErrs} />
        <AntiqueForm 
        submitHandler={createSubmitHandler}
        initialFields={initialFields}
         />
    </div>
        )
}
